import React, { useEffect, useState } from "react";
import axios from "axios";
import "./FfbReupload.css"; // Import the CSS file

const FfbReupload = () => {
  const [werksList, setWerksList] = useState([]);
  const [werks, setWerks] = useState("");
  const [dateFrom, setDateFrom] = useState("");
  const [dateTo, setDateTo] = useState("");
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  useEffect(() => {
    axios.get("/api/ffb-reupload/werks")
      .then(res => setWerksList(res.data))
      .catch(() => setWerksList([]));
  }, []);

  const handleSearch = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");
    try {
      const res = await axios.post("/api/ffb-reupload/search", { werks, dateFrom, dateTo });
      setTickets(res.data);
    } catch {
      setTickets([]);
      setMessage("Error fetching tickets.");
    }
    setLoading(false);
  };

  const handleReupload = async () => {
    setLoading(true);
    setMessage("");
    try {
      await axios.post("/api/ffb-reupload/reupload", { werks, dateFrom, dateTo });
      setMessage("FFB tickets reuploaded successfully.");
    } catch {
      setMessage("Error reuploading tickets.");
    }
    setLoading(false);
  };

  return (
    <div className="page-container">
      <h2>FFB Reupload</h2>
      <form onSubmit={handleSearch} className="search-form">
        <label>
          WERKS:
          <select value={werks} onChange={e => setWerks(e.target.value)} required>
            <option value="">Select WERKS</option>
            {werksList.map(w => (
              <option key={w} value={w}>{w}</option>
            ))}
          </select>
        </label>
        <label>
          Date From:
          <input type="date" value={dateFrom} onChange={e => setDateFrom(e.target.value)} required />
        </label>
        <label>
          Date To:
          <input type="date" value={dateTo} onChange={e => setDateTo(e.target.value)} required />
        </label>
        <button type="submit" disabled={loading}>Search</button>
      </form>
      {message && <div className="message">{message}</div>}
      {tickets.length > 0 && (
        <>
          <button className="reupload-btn" onClick={handleReupload} disabled={loading}>Reupload</button>
          <table className="tickets-table">
            <thead>
              <tr>
                <th>Ticket No</th>
                <th>WERKS</th>
                <th>WB_DATE_IN</th>
                <th>ZSAPFLAG</th>
                {/* Add more columns as needed */}
              </tr>
            </thead>
            <tbody>
              {tickets.map((t, idx) => (
                <tr key={idx}>
                  <td>{t.WB_TICKET}</td>
                  <td>{t.WERKS}</td>
                  <td>{t.WB_DATE_IN?.slice(0, 10)}</td>
                  <td>{t.ZSAPFLAG}</td>
                  {/* Add more columns as needed */}
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}
      {loading && <div className="loading">Loading...</div>}
    </div>
  );
};

export default FfbReupload;
