import React, { useEffect, useState } from "react";
import axios from "axios";
import { FaSearch, FaCheck, FaEdit, FaExclamationTriangle, FaSyncAlt } from "react-icons/fa";
import "../../App.css";

const CPUpdate = () => {
  const [wbTicket, setWbTicket] = useState("");
  const [cplate, setCPlate] = useState("");
  const [viewResult, setViewResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [updating, setUpdating] = useState(false);
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");
  const [showMessage, setShowMessage] = useState(false);

  // New state for table selection and dropdowns
  const [table, setTable] = useState("WB_OUT");
  const [mwerksList, setMWerksList] = useState([]);
  const [werksList, setWerksList] = useState([]);
  const [mwerks, setMWerks] = useState("");
  const [werks, setWerks] = useState("");

  // Fetch MWERKS and WERKS on mount
  useEffect(() => {
    axios.get("/api/cp-update/mwerks").then(res => setMWerksList(res.data));
    axios.get("/api/cp-update/werks").then(res => setWerksList(res.data));
  }, []);

  // View car plate info
  const handleView = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    setMessage("");
    setViewResult(null);
    try {
      let res;
      if (table === "WB_OUT") {
        if (!mwerks) {
          setError("Please select MWERKS.");
          setLoading(false);
          return;
        }
        res = await axios.get("/api/cp-update/view-car-plate-out", {
          params: { wb_ticket: wbTicket, mwerks }
        });
      } else {
        if (!werks) {
          setError("Please select WERKS.");
          setLoading(false);
          return;
        }
        res = await axios.get("/api/cp-update/view-car-plate-in", {
          params: { wb_ticket: wbTicket, werks }
        });
      }
      setViewResult(res.data);
      if (!res.data) {
        setError("No data found for the given WB_TICKET and selected plant.");
      }
    } catch (err) {
      setError("Error fetching car plate details.");
    }
    setLoading(false);
  };

  // Update car plate info
  const handleUpdate = async (e) => {
    e.preventDefault();
    if (!window.confirm("Are you sure you want to update the car plate number?")) return;
    setUpdating(true);
    setError("");
    setMessage("");
    try {
      if (table === "WB_OUT") {
        await axios.post("/api/cp-update/update-car-plate-out", {
          wb_ticket: wbTicket,
          cplate,
          mwerks,
        });
      } else {
        await axios.post("/api/cp-update/update-car-plate-in", {
          wb_ticket: wbTicket,
          cplate,
          werks,
        });
      }
      setMessage("Car plate updated successfully.");
      setShowMessage(true);
      // Refresh view after update
      await handleView(new Event("submit"));
      setTimeout(() => setShowMessage(false), 3000);
    } catch (err) {
      setError("Error updating car plate.");
    }
    setUpdating(false);
  };

  return (
    <div className="page-container">
      <h2>
        <FaEdit className="icon" /> Car Plate Update
      </h2>
      <form className="search-form" onSubmit={handleView} noValidate>
        <label>
          Table:
          <select value={table} onChange={e => { setTable(e.target.value); setViewResult(null); setError(""); }}>
            <option value="WB_OUT">WB_OUT</option>
            <option value="WB_IN">WB_IN</option>
          </select>
        </label>
        <label>
          WB_TICKET:
          <input
            type="text"
            value={wbTicket}
            onChange={e => setWbTicket(e.target.value)}
            required
            maxLength={20}
            style={{ textTransform: "uppercase" }}
          />
        </label>
        {table === "WB_OUT" ? (
          <label>
            MWERKS:
            <select value={mwerks} onChange={e => setMWerks(e.target.value)} required>
              <option value="">Select MWERKS</option>
              {mwerksList.map(mw => (
                <option key={mw} value={mw}>{mw}</option>
              ))}
            </select>
          </label>
        ) : (
          <label>
            WERKS:
            <select value={werks} onChange={e => setWerks(e.target.value)} required>
              <option value="">Select WERKS</option>
              {werksList.map(wk => (
                <option key={wk} value={wk}>{wk}</option>
              ))}
            </select>
          </label>
        )}
        <button
          type="submit"
          className="btn btn-icon"
          disabled={loading || !wbTicket || (table === "WB_OUT" ? !mwerks : !werks)}
        >
          <FaSearch className="icon" />
          {loading ? (
            <>
              <span className="spinner" /> Searching...
            </>
          ) : (
            "View"
          )}
        </button>
      </form>
      {error && (
        <div className="message" style={{ color: "#d32f2f" }}>
          <FaExclamationTriangle className="icon" /> {error}
        </div>
      )}
      {(showMessage || message) && (
        <div className="message">
          <FaCheck className="icon" /> {message}
        </div>
      )}
      {viewResult && (
        <div style={{ marginTop: 24 }}>
          <h4>
            <FaSyncAlt className="icon" /> Car Plate Info
          </h4>
          <div style={{ overflowX: "auto" }}>
            <table className="tickets-table">
              <thead>
                <tr>
                  <th>Table</th>
                  <th>CPLATE</th>
                  <th>ZSAPFLAG</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>{table}</td>
                  <td>{viewResult.CPLATE || "-"}</td>
                  <td>{viewResult.ZSAPFLAG || "-"}</td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>
      )}
      <form className="search-form" onSubmit={handleUpdate} style={{ marginTop: 32 }}>
        <label>
          New Car Plate Number:
          <input
            type="text"
            value={cplate}
            onChange={e => setCPlate(e.target.value.toUpperCase())}
            required
            maxLength={10}
            style={{ textTransform: "uppercase" }}
          />
        </label>
        <button
          type="submit"
          className="btn btn-success btn-icon"
          disabled={
            updating ||
            !wbTicket ||
            !cplate ||
            (table === "WB_OUT" ? !mwerks : !werks)
          }
        >
          <FaEdit className="icon" />
          {updating ? (
            <>
              <span className="spinner" /> Updating...
            </>
          ) : (
            "Update Car Plate"
          )}
        </button>
      </form>
    </div>
  );
};

export default CPUpdate;
