import React, { useEffect, useState } from "react";
import axios from "axios";
import "./SplitSO.css";

const SplitSO = () => {
  const [mwerksList, setMWerksList] = useState([]);
  const [mwerks, setMWerks] = useState("");
  const [date, setDate] = useState("");
  const [wbTickets, setWbTickets] = useState([]);
  const [wbTicket, setWbTicket] = useState("");
  const [ticketDetail, setTicketDetail] = useState(null);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  // Converts 'MM/DD/YYYY' or 'DD/MM/YYYY' to 'YYYY-MM-DD'
  function toISODate(str) {
    if (!str) return "";
    const parts = str.split("-");
    if (parts.length === 3 && parts[0].length === 4) return str; // already ISO
    // If input is 'YYYY-MM-DD', return as is
    // If input is 'MM/DD/YYYY' or 'DD/MM/YYYY', convert
    const d = new Date(str);
    if (isNaN(d)) return str;
    return d.toISOString().slice(0, 10);
  }

  // Fetch MWERKS on mount
  useEffect(() => {
    axios
      .get("/api/split-so/mwerks")
      .then((res) => {
        if (Array.isArray(res.data)) {
          setMWerksList(res.data);
        } else if (res.data && Array.isArray(res.data.mwerks)) {
          setMWerksList(res.data.mwerks);
        } else {
          setMWerksList([]);
        }
      })
      .catch(() => setMWerksList([]));
  }, []);

  // Fetch WB_TICKETs when MWERKS and date are selected
  useEffect(() => {
    setWbTicket("");
    setWbTickets([]);
    setTicketDetail(null);
    setMessage("");
    if (mwerks && date) {
      const isoDate = toISODate(date);
      console.log("Frontend sending date:", isoDate); // Debug log
      axios
        .get("/api/split-so/wb-tickets", {
          params: { mwerks, WBDATE_IN: isoDate },
        })
        .then((res) => {
          setWbTickets(Array.isArray(res.data) ? res.data : []);
        })
        .catch(() => setWbTickets([]));
    }
  }, [mwerks, date]);

  // Clear ticket detail and message when ticket changes
  useEffect(() => {
    setTicketDetail(null);
    setMessage("");
  }, [wbTicket]);

  // Search for ticket detail
  const handleSearch = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");
    setTicketDetail(null);
    try {
      const res = await axios.post("/api/split-so/search", {
        mwerks,
        dateFrom: date,
        dateTo: date,
        wb_ticket: wbTicket,
      });
      setTicketDetail(res.data && res.data.length > 0 ? res.data[0] : null);
      if (!res.data || res.data.length === 0) {
        setMessage("No ticket found for the selected criteria.");
      }
    } catch {
      setMessage("Error searching ticket.");
    }
    setLoading(false);
  };

  // Amend to single SO
  const handleAmend = async () => {
    setLoading(true);
    setMessage("");
    try {
      await axios.post("/api/split-so/amend-single-so", {
        mwerks,
        wb_ticket: wbTicket,
      });
      setMessage("Splitting amended to single SO successfully.");
    } catch {
      setMessage("Error amending splitting.");
    }
    setLoading(false);
  };

  return (
    <div className="page-container">
      <h2>Split SO - Amend to Single SO</h2>
      <form className="search-form" onSubmit={handleSearch}>
        <label>
          MWERKS:
          <select
            value={mwerks}
            onChange={(e) => setMWerks(e.target.value)}
            required
          >
            <option value="">Select MWERKS</option>
            {mwerksList.map((w) => (
              <option key={w} value={w}>
                {w}
              </option>
            ))}
          </select>
        </label>
        <label>
          WBDATE_IN:
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            required
          />
        </label>
        <label>
          WB_TICKET:
          <select
            value={wbTicket}
            onChange={(e) => setWbTicket(e.target.value)}
            required
            disabled={!mwerks || !date}
          >
            <option value="">Select WB_TICKET</option>
            {wbTickets.map((t) => (
              <option key={t} value={t}>
                {t}
              </option>
            ))}
          </select>
          {mwerks && date && wbTickets.length === 0 && (
            <span style={{ color: "red", fontSize: 12 }}>No tickets found.</span>
          )}
          {/* Debug: */}
          <div style={{ fontSize: 10, color: "#888" }}>Tickets: {JSON.stringify(wbTickets)}</div>
        </label>
        <button
          type="submit"
          disabled={loading || !mwerks || !date || !wbTicket}
        >
          Search
        </button>
      </form>
      {message && <div className="message">{message}</div>}
      {loading && <div className="loading">Processing...</div>}
      {ticketDetail && (
        <div style={{ marginTop: 24 }}>
          <h4>Ticket Detail</h4>
          <table className="tickets-table">
            <thead>
              <tr>
                {Object.keys(ticketDetail).map((key) => (
                  <th key={key}>{key}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              <tr>
                {Object.values(ticketDetail).map((val, idx) => (
                  <td key={idx}>{val}</td>
                ))}
              </tr>
            </tbody>
          </table>
          <button
            className="reupload-btn"
            style={{ marginTop: 16 }}
            onClick={handleAmend}
            disabled={loading}
          >
            Amend to Single SO
          </button>
        </div>
      )}
    </div>
  );
};

export default SplitSO;
