import React, { useEffect, useState } from "react";
import axios from "axios";
import "./SplitSO.css";

const SplitSO = () => {
  const [mwerksList, setMWerksList] = useState([]);
  const [mwerks, setMWerks] = useState("");
  const [date, setDate] = useState("");
  const [wbTickets, setWbTickets] = useState([]);
  const [wbTicket, setWbTicket] = useState("");
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  // Fetch MWERKS on mount
  useEffect(() => {
    axios
      .get("/api/split-so/mwerks")
      .then((res) => setMWerksList(res.data))
      .catch(() => setMWerksList([]));
  }, []);

  // Fetch WB_TICKETs when MWERKS and date are selected
  useEffect(() => {
    if (mwerks && date) {
      setWbTicket("");
      setWbTickets([]);
      axios
        .get("/api/split-so/wb-tickets", {
          params: { mwerks, wb_date_in: date },
        })
        .then((res) => setWbTickets(res.data))
        .catch(() => setWbTickets([]));
    }
  }, [mwerks, date]);

  const handleAmend = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");
    try {
      await axios.post("/api/split-so/amend-single-so", {
        mwerks,
        wb_ticket: wbTicket,
      });
      setMessage("Splitting amended to single SO successfully.");
    } catch {
      setMessage("Error amending splitting.");
    }
    setLoading(false);
  };

  return (
    <div className="page-container">
      <h2>Split SO - Amend to Single SO</h2>
      <form className="search-form" onSubmit={handleAmend}>
        <label>
          MWERKS:
          <select
            value={mwerks}
            onChange={(e) => setMWerks(e.target.value)}
            required
          >
            <option value="">Select MWERKS</option>
            {mwerksList.map((w) => (
              <option key={w} value={w}>
                {w}
              </option>
            ))}
          </select>
        </label>
        <label>
          WB_DATE_IN:
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            required
          />
        </label>
        <label>
          WB_TICKET:
          <select
            value={wbTicket}
            onChange={(e) => setWbTicket(e.target.value)}
            required
          >
            <option value="">Select WB_TICKET</option>
            {wbTickets.map((t) => (
              <option key={t} value={t}>
                {t}
              </option>
            ))}
          </select>
        </label>
        <button
          type="submit"
          disabled={loading || !mwerks || !date || !wbTicket}
        >
          Amend to Single SO
        </button>
      </form>
      {message && <div className="message">{message}</div>}
      {loading && <div className="loading">Processing...</div>}
    </div>
  );
};

export default SplitSO;
